    public boolean isBalanced(TreeNode root) {
        Integer check = getHeight(root);
        return (check != null);
    }
    
    public Integer getHeight(TreeNode node){
        if(node == null){
            return 0;
        }
        Integer left = getHeight(node.left);
        if(left == null){
            return null;
        }
        Integer right = getHeight(node.right);
        if(right == null){
            return null;
        }
        if(Math.abs(left - right) > 1){
            return null;
        }
        return Math.max(left, right) + 1;
    }
    
    public boolean getHeight2(TreeNode node){
        if(node == null){
            return true;
        }
        if( !getHeight2(node.left) || !getHeight2(node.right) ){
            return false;
        }
        int left = node.left == null ? 0 : node.left.val;
        int right = node.right == null ? 0 : node.right.val;
        if(Math.abs(left - right) > 1){
            return false;
        }
        node.val = Math.max(left, right) + 1;
        return true;
    }
